<?php

/**
 * @param string $method
 * @param array $param
 * @return mixed
 */
function call($method, $params = [])
{
    $time = !in_array($method, ["sendfrom", "getnewaddress"]) ? now()->addMinutes(5) : 0;
    return cache()->remember(md5(json_encode([$method, $params])), $time, function () use ($method, $params) {
        return app(\App\Foundation\Base::class)->client->$method($params);
    });
}

function price($to = "btc", $coin = 5708)
{
    return cache()->remember("price.$coin.$to", now()->addHour(), function () use ($coin, $to) {
        return last(json_decode(file_get_contents("https://www.coingecko.com/price_charts/$coin/$to/max.json"), 1)["stats"])[1];
    });
}

